import React from 'react';
import {Link, BrowserRouter} from 'react-router-dom';
import logo_nihar from './img/logo_nihar.png';
import bitmoji from './img/bitmoji.jpg';
import nihr from './img/nihr.JPG';
import Particles from 'react-particles-js';

function App() {
  return (
    <BrowserRouter>  
      <div className="App">
      <Particles 
            params={{ 
              particles: { 
                number: { 
                  value: 200, 
                  density: { 
                    enable: true, 
                    value_area: 1000, 
                  } 
                }, 
              }, 
            }} 
          />
        <div className="home-section">
        <nav>
          <div className="nav-wrapper">
            <a href="#!" className="brand-logo">
              <img src={logo_nihar} className="logo-nihar"></img>
            </a>
            <a href="#" data-target="mobile-demo" className="sidenav-trigger"><i className="material-icons">menu</i></a>
            <ul className="right hide-on-med-and-down">
              <li><a href="sass.html">Sass</a></li>
              <li><a href="badges.html">Components</a></li>
              <li><a href="collapsible.html">Javascript</a></li>
              <li><a href="mobile.html">Mobile</a></li>
            </ul>
          </div>
        </nav>

        <ul className="sidenav" id="mobile-demo">
          <li><a href="sass.html">Sass</a></li><hr />
          <li><a href="badges.html">Components</a></li><hr />
          <li><a href="collapsible.html">Javascript</a></li><hr />
          <li><a href="mobile.html">Mobile</a></li>
        </ul>
          <div className="row">
            <div className="col s12 m6 home-img">
              <img src={nihr} className="nihr"></img>
            </div>
            <div className="col s12 m6 home-text">
              <h3>Hello</h3>
              <h1>I am Nihar<br/>Srivastava</h1>
            </div>
          </div>

        </div>
      </div>

    </BrowserRouter>
  );
}

export default App;
